import React from 'react'
import styles from './header.module.scss'

const __Dark_Theme_Key = '__dark';
export const __Dark_Theme_Name = 'theme_dark';

const Header = () => {
  React.useEffect(() => {
    const isDark = localStorage.getItem(__Dark_Theme_Key) === 'true'
    const existingClasses = document.documentElement.className;

    if(isDark) {
      const newClasses = (existingClasses + ' ' + __Dark_Theme_Name).trim()
      document.documentElement.className = newClasses
    }
  })

  const themeToggle = () => {
    const existingClasses = document.documentElement.className;
    console.log('existing:', existingClasses);
    const isDark = existingClasses.indexOf(__Dark_Theme_Name) > -1;
    console.log('is dark: ', isDark);
    localStorage.setItem(__Dark_Theme_Key, JSON.stringify(isDark))

    if(isDark) {
      document.documentElement.className = existingClasses.replace(new RegExp(__Dark_Theme_Name, 'gi'), '')
    } else {
      document.documentElement.className = existingClasses + ' ' + __Dark_Theme_Name
    }
  }

  return (
    <header className={styles.header}>
      Rick
      <span>And</span>
      Morty

      <button onClick={themeToggle}>Toggle Dark Mode</button>
    </header>
  )
}

export default Header;
