import React from 'react'

const initialState = {
  toggle: () => {}
}
const ThemeContext = React.createContext(initialState)

function ThemeProvider({ children }) {
  // Default theme is light
  const __Dark_Theme_Key = '__dark';
  const __Dark_Theme_Name = 'theme_dark';

  // On mount, read the preferred theme from the persistence
  React.useEffect(() => {
    const isDark = localStorage.getItem(__Dark_Theme_Key) === 'true'
    const existingClasses = document.documentElement.className;

    if(isDark) {
      document.documentElement.className = existingClasses + ' ' + __Dark_Theme_Name
    }
  })

  // To toggle between dark and light modes
  const toggle = () => {
    const existingClasses = document.documentElement.className;
    const isDark = existingClasses.indexOf(__Dark_Theme_Name) > -1;
    localStorage.setItem(__Dark_Theme_Key, JSON.stringify(isDark))
    if(isDark) {
      document.documentElement.classList.add(__Dark_Theme_Name)
    } else {
      document.documentElement.classList.remove(__Dark_Theme_Name)
    }
  }

  return (
    <ThemeContext.Provider value={{ toggle }}>
      {children}
    </ThemeContext.Provider>
  )
}

export { ThemeProvider, ThemeContext }
